mapper.update:
update dts_goods_product
set number = number + #{num,jdbcType=INTEGER}, update_time = now()
where id = #{id,jdbcType=INTEGER}

update dts_goods_product
set number = number - #{num,jdbcType=INTEGER}, update_time = now()
where id = #{id,jdbcType=INTEGER} and number >= #{num,jdbcType=INTEGER}

