set browse = browse + #{num,jdbcType=INTEGER}, update_time = now()
where id = #{id,jdbcType=INTEGER}

update dts_goods
set sales = sales + #{num,jdbcType=INTEGER}, update_time = now()
where id = #{id,jdbcType=INTEGER}
